# Generate file names for the tables
generate_table_fns <- function(tables) {
  file_names <- list()
  for (name in names(tables)) {
    folder_path <- paste0("tables/", tables[[name]], "/")
    if (!dir.exists(folder_path)) {
      dir.create(folder_path, recursive = TRUE)
    }
    file_names[[name]] <- list(
      html = paste0(folder_path, tables[[name]], ".html"),
      tsv = paste0(folder_path, tables[[name]], ".tsv.zip"),
      docx = paste0(folder_path, tables[[name]], ".docx"),
      csv = paste0(folder_path, tables[[name]], ".csv")
    )
  }
  return(file_names)
}

table_fns <- generate_table_fns(tables)
print(table_fns)

# Generate file names for the figures
generate_figure_fns <- function(figures) {
  file_names <- list()
  for (name in names(figures)) {
    folder_path <- paste0("figures/", figures[[name]], "/")
    if (!dir.exists(folder_path)) {
      dir.create(folder_path, recursive = TRUE)
    }
    file_names[[name]] <- list(
      png = paste0(folder_path, figures[[name]], ".png"),
      eps = paste0(folder_path, figures[[name]], ".eps"),
      jpg = paste0(folder_path, figures[[name]], ".jpg")
    )
  }
  return(file_names)
}

figure_fns <- generate_figure_fns(figures)
print(figure_fns)

sup_figure_fns <- generate_figure_fns(sup_figures)
